// Get unique libraries
export const getLibraries = (): string[] => {
  return [...new Set(animations.map(anim => anim.library))].sort();
};

// Get animations by library
export const getAnimationsByLibrary = (library: string): Animation[] => {
  return animations.filter(anim => anim.library === library);
};

// Get animation by library and name
export const getAnimation = (library: string, name: string): Animation | undefined => {
  return animations.find(anim => anim.library === library && anim.name === name);
};

// Get video path for an animation
export const getVideoPath = (library: string, name: string): string => {
  return `http://assets.open.mp/assets/anims/${library}/${name}.webm`;
};

